mapper.select:

SELECT id, brandId, sex, title, price, maincategory_id, subcategory_id, shortdetail, longdetail
FROM product
WHERE id like #{id}


SELECT product_id, color, imagepath
FROM ProductColor
WHERE ProductColor.product_id like #{product_id}


SELECT product_id, sizelabel
FROM ProductSize
WHERE ProductSize.product_id like #{product_id}
